import React from 'react';
import styles from './Header.module.scss';

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchPhrase: '',
      searchOnlyCars: false,
      searchEngineType: '',
    };
  }

  handleSearchPhraseChange = (event) => {
    this.setState({ searchPhrase: event.target.value }, () =>
      this.filterVehicles()
    );
  };

  handleOnlyCarsChange = (event) => {
    this.setState({ searchOnlyCars: event.target.checked }, () =>
      this.filterVehicles()
    );
  };

  handleSelectEngineType = (event) => {
    this.setState({ searchEngineType: event.target.value }, () =>
      this.filterVehicles()
    );
  };

  filterVehicles = () => {
    const { produkty } = this.props;
    const { searchPhrase, searchOnlyCars } = this.state;

    // odfiltrowanie zgodnych wynik贸w
    let filteredVehicles = produkty.filter((produkty) =>
    produkty.nazwa.includes(searchPhrase)
    );
    if (searchOnlyCars) {
      filteredVehicles = filteredVehicles.filter(
        (produkty) => produkty.kategoria === 'car'
      );
    }
   
    console.log('sprawdzam aktualne filtry', filteredVehicles);
    // przekazanie wyfiltrowanych pojazd贸w do komponentu rodzica (App)
    this.props.sendFilteredVehiclesToParentComponent(filteredVehicles);
  };

  handleResetFilters = () => {
    this.setState(
      {
        searchPhrase: '',
        searchOnlyCars: false,
        searchEngineType: '',
      },
      () => {
        this.filterVehicles();
      }
    );
  };

  getUniqueFoodCategory = () => {
    const { produkty } = this.props;
    const vehicleEngineList = produkty.map((produkty) => produkty.kategoria);
    const foodCategory = [...new Set(vehicleEngineList)];
    return foodCategory;
  };

  render() {
    const uniqueFoodCategory = this.getUniqueFoodCategory();
    const { searchPhrase, searchOnlyCars, searchEngineType } = this.state;
    return (
      <div className={styles.HeaderWrapper}>
        <input
          value={searchPhrase}
          onChange={this.handleSearchPhraseChange}
        ></input>
        <p> Tylko produkty </p>
        <input
          type="checkbox"
          onChange={this.handleOnlyCarsChange}
          value={searchOnlyCars}
        ></input>
        <p> kategoria </p>
        <select value={searchEngineType} onChange={this.handleSelectEngineType}>
          <option key={'all'} value={''}>
            All types
          </option>
          {uniqueFoodCategory.map((kategoria) => (
            <option key={kategoria} value={kategoria}>
              {kategoria}
            </option>
          ))}
        </select>
        {/* <button onClick={this.filterVehicles}>Wyszukaj</button> */}
        <button onClick={this.handleResetFilters}>Zresetuj filtry</button>
      </div>
    );
  }
}

export default Header;
