import React from 'react';
import { useState } from "react";
import ProductsList from './components/ProductsList/ProductsList';
import ShopingList from './components/ShopingList/ShopingList';
import ProductsFilters from './components/ProductsFilters/ProductsFilters';
import produkty from './common/consts/produkty';
import styles from './App.module.scss';

function App() {
  const cart = [];

  const [products, setProducts] = useState(produkty); //1
  const [shoppingList, setShoppingList] = useState(cart); //2
  const [productsToDisplay, setProductsToDisplay] = useState(products); //3
  // const [resetFilters, setResetFilters] = useState(false); //4

  // const addToShoppingList = (product) => {

  //   setShoppingList((state) => [
  //     ...state,
  //     { ...product, id: Math.random(), isClicked: false },
  //   ]);
  // };

  const lineThroughProduct = (index) => {
    setShoppingList((prev) =>
      prev.map((e, i) => (i === index ? { ...e, isClicked: !e.isClicked } : e))
    );
  };



  return (
    <div className={styles.appWrapper}>
      <ProductsFilters
        produkty={products}
        // sendfilteredProductsToParentComponent={setProducts}
        sendFilteredProductsToParentComponent={setProductsToDisplay}
        // reset={resetFilters}
      />
      <div className={styles.columnsWrapper}>
        <ProductsList
          // addToShoppingList={addToShoppingList}
          products={productsToDisplay}
          sendResultsToParent={(resultsFromShoppingList) => setShoppingList(resultsFromShoppingList)}
        />
        <ShopingList
          shoppingList={shoppingList}
          remove={setShoppingList}
          lineThrough={lineThroughProduct}
        />
      </div>
    </div>
  );
};



export default App;
