import React from 'react';
import { useState } from "react";
import ProductsList from './components/ProductsList/ProductsList';
import ShopingList from './components/ShopingList/ShopingList';
import ProductsFilters from './components/ProductsFilters/ProductsFilters';
import produkty from './common/consts/produkty';
import styles from './App.module.scss';

function App() {
  const [prodcuts, setProducts] = useState(produkty);
  const [shoppingList, setShoppingList] = useState([]); 
  const [productsToDisplay, setProductsToDisplay] = useState(products);
  const [resetFilters, setResetFilters] = useState(false);

  const addToShoppingList = (product) => {
    setShoppingList((state) => [
      ...state,
      { ...product, id: Math.random(), isClicked: false },
    ]);
  };

  return (
    <div className={styles.appWrapper}>
      <ProductsFilters
        produkty={produkty}
        sendfilteredProductsToAppComponent={setProducts}
      />
      <div className={styles.columnsWrapper}>


        <ProductsList
          productList={prodcuts}
          sendResultsToParent={(addedProduct) => setShoppingList([...ListOnRight],addedProduct)}
        />


        <ShopingList
             shoppingList={shoppingList}
             remove={setShoppingList}
        />
      </div>
    </div>
  );
}

export default App;
