import React, { useState } from 'react';
import ProductsList from './components/ProductsList/ProductList';
import ShopingList from './components/ShopingList/ShopingList';
import AddProducts from './components/AddProducts/AddProducts';
import styles from './App.module.scss';

function App() {
  const leftColumnText = 'Left column';
  const rightColumnText = 'Right column';

  const [timerValue, setTimerValue] = useState(0);

  setTimeout(() => {
    setTimerValue(timerValue + 1);
  }, 1000);

  return (
    <div className={styles.appWrapper}>
      <AddProducts timerValue={timerValue} />
      <div className={styles.columnsWrapper}>
        {timerValue < 10 || timerValue > 15 ? (
          <ProductsList timerValue={timerValue} text={leftColumnText} />
        ) : null}
        <ShopingList timerValue={timerValue} text={rightColumnText} />
        {/* {timerValue < 10 || timerValue > 15 ? (
          <RightColumn timerValue={timerValue} text={rightColumnText} />
        ) : null} */}
      </div>
    </div>
  );
}

export default App;
