import React from 'react';
// import styles from './Results.module.scss';
import commonColumnsStyles from '../../common/styles/Columns.module.scss';

class ProductsList extends React.Component {


  // addProduct = (event) => {
  //   this.setState({ shoppingCart: [...this.state.shoppingCart, event.target.innerText] });
  //   console.log(this.state.shoppingCart);
  //   this.props.sendResultsToParent(this.state.shoppingCart);
  // }

  addProduct = (products) => {
    console.log(this.state.shoppingCart);
    this.props.sendResultsToParent(this.props.shoppingCart, products.nazwa);
  }

  render() {
    const { productList } = this.props;
    return (
      <div className={commonColumnsStyles.App}>
        <header className={commonColumnsStyles.AppHeader}>

          <ul>
            {productList.map((produkty, index) => (

              <li 
              
              key={index} 
              onClick={()=>this.addProduct(index)} 
              
              > {produkty.nazwa} </li>

            ))}

          </ul>
        </header>
      </div>
    );
  }
}

export default ProductsList;
