import React, { useState } from 'react';
import ProductsList from './components/ProductsList/ProductsList';
import ShopingList from './components/ShopingList/ShopingList';
import ProductsFilters from './components/ProductsFilters/ProductsFilters';
import produkty from './common/consts/produkty';
import styles from './App.module.scss';

function App() {
  const cart = [];
  const [productsListToDisplay, setProductsListToDisplay] = useState(produkty);
  const [koszykToDisplay, setKoszykToDisplay] = useState(cart);

  const deleteProduct = () => {
    setKoszykToDisplay();
  };

  // console.log('wszystkie produkty', produkty);
  return (
    <div className={styles.appWrapper}>
      <ProductsFilters
        produkty={produkty}
        sendfilteredProductsToParentComponent={setProductsListToDisplay}
      />
      <div className={styles.columnsWrapper}>
        <ProductsList
          productsToDisplay={productsListToDisplay}
          sendResultsToParent={(resultsFromShoppingList) => setKoszykToDisplay(resultsFromShoppingList)}
        />
        <ShopingList 
        rightClick={deleteProduct}
        koszykToDisplay={koszykToDisplay} />
      </div>
    </div>
  );
}

export default App;
